import { useEffect, useState } from "react";
import { _avatars } from "../utils/avatars";
import Title from "./common/Title";
import Input from "./common/Input";
import Select from "./common/Select";
import { _colors } from "../utils/colors";
import { warriorAttacks, Warrior } from "../utils/warriors";
import Button from "./common/Button";
import { Cancel, Register, Status } from "../api/axios";
import Dialog from "./common/Dialog";
import {
  DialogContent,
  DialogTitle,
  IconButton,
  Grid,
  Avatar,
} from "@mui/material";
import { errors } from "../utils/errors";
import { Close } from "@mui/icons-material";

interface FightWarrior {
  Name: string;
  WarriorType: number;
  Attacks: number[];
}

interface Round {
  Warrior1Attack: number;
  Warrior2Attack: number;
}

interface Fight {
  ID: string;
  Status: number;
  Warrior1: FightWarrior;
  Warrior2: FightWarrior;
  Rounds: Round[];
  Err?: any;
}

const Home = () => {
  const [warrior, setWarrior] = useState<Warrior>({
    name: "",
    warriorType: 0,
    attacks: [-99, -99, -99],
  });

  const [dialogOpen, setDialogOpen] = useState<boolean>(false);
  const [warriorSelection, setWarriorSelection] = useState<boolean>(false);
  const [waitingScreen, setWaitingScreen] = useState<boolean>(false);
  const [fightingScreen, setFightingScreen] = useState<boolean>(false);
  const [doneScreen, setDoneScreen] = useState<boolean>(false);

  const [message, setMessage] = useState<string>("")

  const [fightResult, setFightResult] = useState<Fight>({
    ID: "",
    Status: 0,
    Warrior1: { Name: "", WarriorType: 0, Attacks: [0] },
    Warrior2: { Name: "", WarriorType: 0, Attacks: [0] },
    Rounds: [{ Warrior1Attack: 0, Warrior2Attack: 0 }],
  });

  const warriorTypeSet: Set<number> = new Set([0, 1, 2, 3, 4]);
  const warriorAttacksSet: Set<number> = new Set([0, 1, 2]);

  const [currentFightId, setCurrentFightId] = useState<string>("");

  useEffect(() => {
    const interval = setInterval(() => {
      if ((waitingScreen || fightingScreen) && currentFightId) {
        Status(currentFightId).then((result) => {
          if (result.data.result.fight.Status === 1) {
            setWaitingScreen(false);
            setFightingScreen(true);
            setDoneScreen(false);
            setFightResult(result.data.result.fight);
          }
          if (result.data.result.fight.Status === 2) {
            setWaitingScreen(false);
            setFightingScreen(false);
            setDoneScreen(true);
            setFightResult(result.data.result.fight);
          }
        });
      }
    }, 1000);
    return () => clearInterval(interval);
  }, [waitingScreen, fightingScreen, currentFightId]);

  const roundWinner = (r: Round): number => {
    switch (r.Warrior1Attack) {
      case 0: {
        if (r.Warrior2Attack === 0) return 0; //paper - paper: tie
        else if (r.Warrior2Attack === 1) return 2; //paper - scissors - w2
        else if (r.Warrior2Attack === 2) return 1; //paper - rock - w1
        break;
      }
      case 1: {
        if (r.Warrior2Attack === 0) return 1; //scissors - paper: w1
        else if (r.Warrior2Attack === 1) return 0; //scissors - scissors - tie
        else if (r.Warrior2Attack === 2) return 2; //scissors - rock - w2
        break;
      }
      case 2: {
        if (r.Warrior2Attack === 0) return 2; //rock - paper: w2
        else if (r.Warrior2Attack === 1) return 1; //scissors - scissors - w1
        else if (r.Warrior2Attack === 2) return 0; //scissors - rock - tie
        break;
      }
    }
    return 99;
  };

  const calculateWinner = () => {
    let winner: number[] = [];
    fightResult.Rounds.map((round) =>
      winner.push(roundWinner(round))
    );
    console.log(winner);
    return winner;
  };

  const calculateMatchWinner = () => {
    let winner: number[] = [];
    let counts: number[] = [0, 0, 0];
    fightResult.Rounds.map((round) =>
      winner.push(roundWinner(round))
    );
    for (const num of winner) {
      counts[num] = counts[num] ? counts[num] + 1 : 1;
    }
    if (counts[1] > 1 || (counts[1] > 0 && counts[0] > 1)) return 1;
    else if (counts[2] > 1 || (counts[2] > 0 && counts[0] > 1)) return 2;
    else if (counts[0] > 1) return 0;
  };

  useEffect(() => {
    if (doneScreen) {
    }
  }, [doneScreen]);

  const validateForm = () => {
    let err: number = 0;
    if (
      warrior.name.length <= 0) err = 1
    else if (
      warrior.name.length >= 26) err = 2
    else if (!warriorTypeSet.has(warrior.warriorType)) err = 3
    else if (warrior.attacks.length !== 3) err = 4
    else if (!warriorAttacksSet.has(warrior.attacks[0])) err = 5
    else if (!warriorAttacksSet.has(warrior.attacks[1])) err = 6
    else if (!warriorAttacksSet.has(warrior.attacks[2])) err = 7
    if (err === 0) {
      Register(warrior).then((result) => {
        if (result.data.result.id) {
          setCurrentFightId(result.data.result.id);
          setWaitingScreen(true);
        }
      });
    } else { setMessage(errors[err-1]); setDialogOpen(true) };
  };

  return (
    <div
      style={{
        textAlign: "center",
        maxWidth: "40vw",
        marginLeft: "30vw",
      }}
    >
      <Dialog open={dialogOpen}>
        <DialogTitle>
          Error{" "}
          <IconButton
            aria-label="close"
            onClick={() => setDialogOpen(false)}
            sx={{
              position: "absolute",
              right: 8,
              top: 8,
            }}
          >
            <Close />
          </IconButton>
        </DialogTitle>
        <DialogContent>{message}</DialogContent>
      </Dialog>
      {warriorSelection ? (
        <>
          <Title>
            <span onClick={() => setWarriorSelection(false)}>{"<"}</span>{" "}
            Tangata toa
          </Title>
          <Grid container>
            {_avatars.map(
              (key, index) =>
                index !== warrior.warriorType && (
                  <Grid
                    key={key + index}
                    item
                    xs={6}
                    style={{
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      padding: 20,
                    }}
                  >
                    <Avatar
                      sx={{ width: 100, height: 100 }}
                      src={_avatars[index]}
                      onClick={() => {
                        setWarrior((old) => {
                          return {
                            ...old,
                            warriorType: index,
                            attacks: [-99, -99, -99],
                          };
                        });
                        setWarriorSelection(false);
                      }}
                    />
                  </Grid>
                )
            )}
          </Grid>
        </>
      ) : waitingScreen ? (
        <>
          <Title>Pakipaki</Title>
          <Grid container>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 35,
                color: _colors.secondary,
              }}
            >
              <div>{warrior.name}</div>
              <Avatar
                sx={{ width: 100, height: 100 }}
                src={_avatars[warrior.warriorType]}
              />
            </Grid>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 35,
                color: _colors.secondary,
              }}
            >
              <div>VS</div>
            </Grid>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 35,
                color: _colors.secondary,
              }}
            >
              <div>Unknown</div>
              <Avatar
                sx={{
                  border: "1px solid #000",
                  width: 100,
                  height: 100,
                  color: _colors.secondary,
                  fontFamily: "inherit",
                  fontSize: 75,
                }}
              >
                ?
              </Avatar>
            </Grid>
          </Grid>

          <div
            style={{
              color: "white",
              background: _colors.green,
              width: "100%",
              textAlign: "center",
              padding: 20,
              marginTop: 20,
              fontSize: 40,
            }}
          >
            Waiting for 2nd player to join
          </div>

          <div style={{ marginTop: 30 }}>
            <Button
              onClick={() => {
                Cancel(currentFightId);
                setCurrentFightId("");
                setWaitingScreen(false);
              }}
            >
              Panic!!!
            </Button>
          </div>
        </>
      ) : fightingScreen && fightResult ? (
        <>
          <Title>Pakipaki</Title>
          <Grid container>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 35,
                color: _colors.secondary,
              }}
            >
              <div>{fightResult.Warrior1.Name}</div>
              <Avatar
                sx={{ width: 100, height: 100 }}
                src={_avatars[fightResult.Warrior1.WarriorType]}
              />
            </Grid>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 35,
                color: _colors.secondary,
              }}
            >
              <div>VS</div>
            </Grid>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 35,
                color: _colors.secondary,
              }}
            >
              <div>{fightResult.Warrior2.Name}</div>
              <Avatar
                sx={{ width: 100, height: 100 }}
                src={_avatars[fightResult.Warrior2.WarriorType]}
              />
            </Grid>
          </Grid>

          <div
            style={{
              color: "white",
              background: _colors.green,
              width: "100%",
              textAlign: "center",
              padding: 20,
              marginTop: 20,
              fontSize: 40,
            }}
          >
            Beating the shit out of{" "}
            {fightResult.Warrior1.Name === warrior.name
              ? fightResult.Warrior2.Name
              : fightResult.Warrior1.Name}
          </div>

          <div style={{ marginTop: 30 }}>
            <Button
              onClick={() => {
                Cancel(currentFightId);
                setCurrentFightId("");
                setWaitingScreen(false);
              }}
            >
              Surrender!!!
            </Button>
          </div>
        </>
      ) : doneScreen ? (
        <>
          <Title>Pakipaki</Title>
          <Grid container>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 35,
                color: _colors.secondary,
              }}
            >
              <div>{fightResult.Warrior1.Name}</div>
              <Avatar
                sx={{
                  width: 100,
                  height: 100,
                  border:
                    calculateMatchWinner() === 1
                      ? "3px solid" + _colors.green
                      : "",
                }}
                src={_avatars[fightResult.Warrior1.WarriorType]}
              />
              {calculateMatchWinner() === 1 && (
                <span style={{ color: _colors.green }}>Winner</span>
              )}
            </Grid>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 75,
                color: _colors.secondary,
              }}
            >
              <div>VS</div>
            </Grid>
            <Grid
              item
              xs={4}
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 35,
                color: _colors.secondary,
              }}
            >
              <div>{fightResult.Warrior2.Name}</div>
              <Avatar
                sx={{
                  width: 100,
                  height: 100,
                  border:
                    calculateMatchWinner() === 2
                      ? "3px solid" + _colors.green
                      : "",
                }}
                src={_avatars[fightResult.Warrior2.WarriorType]}
                onClick={() => setWarriorSelection(true)}
              />
              {calculateMatchWinner() === 2 && (
                <span style={{ color: _colors.green }}>Winner</span>
              )}
            </Grid>
          </Grid>

          <div
            style={{
              color: _colors.secondary,
              width: "100%",
              textAlign: "center",
              padding: 20,
              marginTop: 20,
              fontSize: 40,
            }}
          >
            {fightResult.Rounds.map((round, i) => (
              <div>
                {i + 1}.{" "}
                <span
                  style={{
                    opacity:
                      calculateWinner()[i] === 0 || calculateWinner()[i] === 1
                        ? 1
                        : 0.8,
                  }}
                >
                  {
                    warriorAttacks[fightResult.Warrior1.WarriorType][
                    round.Warrior1Attack
                    ]
                  }
                </span>{" "}
                -{" "}
                <span
                  style={{
                    opacity:
                      calculateWinner()[i] === 0 || calculateWinner()[i] === 2
                        ? 1
                        : 0.8,
                  }}
                >
                  {
                    warriorAttacks[fightResult.Warrior2.WarriorType][
                    round.Warrior2Attack
                    ]
                  }
                </span>
              </div>
            ))}
          </div>

          <div style={{ marginTop: 30 }}>
            <Button
              onClick={() => {
                validateForm();
                setDoneScreen(false);
                setWaitingScreen(true);
              }}
            >
              Again!!!
            </Button>
          </div>
        </>
      ) : (
        <>
          <Title>Whawhai</Title>
          <div style={{ textAlign: "center" }}>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <Avatar
                sx={{ width: 100, height: 100 }}
                src={_avatars[warrior.warriorType]}
                onClick={() => setWarriorSelection(true)}
              />
              <Input
                placeholder="$WARRIOR-NAME"
                value={warrior.name}
                onChange={(e) =>
                  setWarrior((old) => {
                    return { ...old, name: e.target.value };
                  })
                }
              />
            </div>

            <div
              style={{
                marginTop: 20,
              }}
            >
              {warrior.attacks.map((attack, index) => (
                <div
                  key={attack + index}
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  <Select
                    style={{
                      backgroundColor:
                        index === 0
                          ? _colors.secondary
                          : index === 1
                            ? _colors.secondary80
                            : _colors.secondary60,
                    }}
                    value={attack}
                    onChange={(e) =>
                      setWarrior((old) => {
                        return {
                          ...old,
                          attacks: [
                            index === 0
                              ? parseInt(e.target.value)
                              : old.attacks[0],
                            index === 1
                              ? parseInt(e.target.value)
                              : old.attacks[1],
                            index === 2
                              ? parseInt(e.target.value)
                              : old.attacks[2],
                          ],
                        };
                      })
                    }
                  >
                    <option value={-99} disabled>
                      Attack for round #{index + 1}
                    </option>
                    {warriorAttacks[warrior.warriorType].map((key, i) => (
                      <option value={i} key={key + i}>
                        {key}
                      </option>
                    ))}
                  </Select>
                </div>
              ))}
            </div>
            <div style={{ marginTop: 30 }}>
              <Button onClick={validateForm}>Fight!!!</Button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Home;
