import styled from "@emotion/styled";
import { Avatar as MUIAvatar } from "@mui/material";
import { _colors } from "../../utils/colors";

const CssAvatar = styled(MUIAvatar)(({ size, hover }: { size: string, hover?: boolean }) => ({
    width: (size === "unknown" ? 96 : size === "lg" ? 150 : 100),
    height: (size === "unknown" ? 96 : size === "lg" ? 150 : 100),
    // unknown - font
    color: _colors.secondary,
    fontFamily: "inherit",
    fontSize: 75,
    // 
    border: size === "unknown" ? "2px solid #000" : "",
    "&:hover": {
        cursor: hover ? "pointer" : "",
        opacity: hover ? 0.8 : 1
    }
}));

const Avatar = ({ name, size, hover, src, onClick }: { name: string, size: string, hover?: boolean, src?: string, onClick?: () => void }) => {
    return (<>
        <div>{name}</div>
        <CssAvatar size={size} hover={hover} src={src} onClick={onClick}>{size === "unknown" && "?"}</CssAvatar>
    </>)
}

export default Avatar;
