import styled from "@emotion/styled";
import { Avatar as MUIAvatar } from "@mui/material";
import { _colors } from "../../utils/colors";

const CssAvatar = styled(MUIAvatar)(({ size, hover }: { size: string, hover?: boolean }) => ({
    borderRadius: 0,
    border: "none",
    background: _colors.secondary,
    outline: "none",
    fontSize: 30,
    padding: "10px 20px",
    color: _colors.primary,
    fontFamily: "inherit",
    transition: "0.3s",
    "&:hover": {
        cursor: "pointer",
        opacity: 0.9,
    },
}));

const Avatar = ({ name, size, hover, src }: { name: string, size: string, hover?: boolean, src: string }) => {
    return(<>
        <div>{name}</div>
        <CssAvatar size={size} hover={hover} src={src}>{size === "unknown" && "?"}</CssAvatar>
    </>)
}

export default Avatar;
