import { _avatars } from "../utils/avatars";
import Title from "../components/common/Title";
import {Warrior } from "../utils/warriors";
import {
    Avatar, Box, Grid,
} from "@mui/material";
import { _colors } from "../utils/colors";



const SelectWarrior = ({ warrior, setWarriorSelection, setWarrior }: { warrior: Warrior, setWarriorSelection: React.Dispatch<React.SetStateAction<boolean>>, setWarrior: React.Dispatch<React.SetStateAction<Warrior>> }) => {
    return (
        <>
            <Title>
                <span sx={{transition: "0.3s","&:hover": {
                    cursor: "pointer",
                    color: _colors.secondary80
                }}} onClick={() => setWarriorSelection(false)}>{"<"}</span>{" "}
                Tangata toa
            </Title>
            <Grid container>
                {_avatars.map(
                    (key, index) =>
                        index !== warrior.warriorType && (
                            <Grid
                                key={key + index}
                                item
                                xs={6}
                                style={{
                                    display: "flex",
                                    alignItems: "center",
                                    justifyContent: "center",
                                    padding: 20,
                                }}
                            >
                                <Avatar
                                    sx={{ width: 100, height: 100 }}
                                    src={_avatars[index]}
                                    onClick={() => {
                                        setWarrior((old) => {
                                            return {
                                                ...old,
                                                warriorType: index,
                                                attacks: [-99, -99, -99],
                                            };
                                        });
                                        setWarriorSelection(false);
                                    }}
                                />
                            </Grid>
                        )
                )}
            </Grid>
        </>
    );
};

export default SelectWarrior;
